
import { getProviders, signIn } from 'next-auth/react'
import Image from 'next/image'
import React, { useEffect, useState } from 'react'

const Modal = ({ setOpenModal }) => {
    const [providers, setProviders] = useState(null)
    const [user, setUser] = useState({})
    const [exist, setExist] = useState(false)
    useEffect(() => {
        const provider = async () => {
            const response = await getProviders()
            setProviders(response)
        }
        provider()
    }, [])

    const handleChange = async (e) => {
        setExist(false)
        setUser(prev => {
            return { ...prev, [e.target.name]: e.target.value }
        })
        console.log(user)
    }

    const handleRegister = async (e) => {
        e.preventDefault()
        setExist(false)
        try {
            const response = await fetch(`/api/check`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email: user.email }),
            });
            // const data = await response.json();
            // console.log('checkUser', response.ok);

            if (response.ok) {
                setExist(true)
            } else {
                const response = await fetch('/api/register', {
                    method: "POST",
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        email: user.email,
                        password: user.password
                    })
                })
                console.log(user)
                const data = await response.json()
                console.log('this data saved:', data)
                if (response.ok) {
                    setOpenModal(false)
                }
                setExist(false)
            }


        } catch (err) {
            console.log(err)
        }
    }
    return (
        <div className="absolute h-full w-full z-40 flex justify-center items-center">
            <div className="absolute flex h-full w-full items-center justify-center bg-black opacity-60 ">
                {/* Parent Overlay */}
            </div>

            <div className="absolute h-content bg-white text-center indent-1 opacity-100 flex gap-2 flex-col">
                {/* Child Modal */}
                <header className="m-2 flex justify-start gap-[13.5rem] items-center ">
                    <span className="cursor-pointer font-mono text-2xl font-extrabold" onClick={() => setOpenModal(false)}>&times;</span>
                    <h2>Sign In</h2>
                </header>
                {exist && <h3 className='text-red-600 my-2'>you already registerd!</h3>}
                <hr className="" />
                <form className="mt-5 flex flex-col justify-start gap-4" onSubmit={handleRegister}>
                    <span className="flex flex-col gap-1 ">
                        <label className="self-start ml-2" htmlFor="email">Email</label>
                        <input
                            className="mx-3 px-1 ring-1 ring-gray-300 h-10"
                            type="email"
                            placeholder="email"
                            name='email'
                            onChange={(e) => handleChange(e)}
                        />
                    </span>

                    <span className="flex flex-col ">
                        <label className="self-start ml-2" htmlFor="password">Password</label>
                        <input
                            className="mx-3 px-1 ring-1 ring-gray-300 h-10"
                            type="password"
                            placeholder="password"
                            name='password'
                            onChange={(e) => handleChange(e)}
                        />
                    </span>
                    <button className="bg-blue-500 text-white  mx-2 py-2 px-2 rounded-md cursor-pointer" type="submit">Sign In</button>
                </form>

                {/* login with social  */}
                <div className='flex flex-col gap-4  py-3 items-center mx-2'>

                    <p className='w-full cursor-pointer bg-blue-500 flex py-1 px-4 text-white justify-between rounded-md '
                        onClick={() => {
                            signIn(providers.facebook.id)
                        }}
                    >
                        <Image className='cursor-pointer' src='data:image/png;base64,' height={30} width={30} alt='google' />
                        <span className='border-left border-white'>Facebook</span>
                    </p>

                    <p className='w-full cursor-pointer bg-red-500 flex py-1 px-4 text-white justify-between rounded-md '
                        onClick={() => {
                            console.log(providers.google.id)
                            signIn(providers.google.id)
                        }}
                    >
                        <Image className='rounded-full ' src='data:image/png;base64,' height={30} width={30} alt='google' />
                        <span className='border-left border-white'>Google</span>
                    </p>

                </div>
            </div>
        </div>
    )
}

export default Modal